// Success handler - Process successful content generation
// Parses the JSON output from orchestrate-content.js

const result = JSON.parse($input.first().json.stdout);

return [{
  json: {
    status: 'success',
    message: `Successfully generated post: ${result.file}`,
    file: result.file,
    commit: result.commit,
    timestamp: result.timestamp || new Date().toISOString()
  }
}];

// Error handler - Process and categorize errors from orchestrate-content.js
// Extracts structured error codes for routing and logging

const input = $input.first().json;
let errorCode = 'UNKNOWN_ERROR';
let errorMessage = 'Unknown error';
let timestamp = new Date().toISOString();

try {
  if (input.stdout) {
    const result = JSON.parse(input.stdout);
    errorCode = result.error || 'UNKNOWN_ERROR';
    errorMessage = result.message || result.error || 'Unknown error';
    timestamp = result.timestamp || timestamp;
  } else if (input.stderr) {
    errorMessage = input.stderr;
  }
} catch (e) {
  errorMessage = input.stderr || input.stdout || 'Failed to parse error';
}

// Categorize error for potential retry logic
const isRetryable = ['GIT_PUSH_FAILED', 'POST_GENERATION_FAILED'].includes(errorCode);

return [{
  json: {
    status: 'error',
    errorCode: errorCode,
    message: errorMessage,
    exitCode: input.exitCode,
    timestamp: timestamp,
    isRetryable: isRetryable
  }
}];
